t-weight: 700;\n            line-height: normal;\n            padding: 35px 0 0;\n            color: #4d4d4d;\n        }\n\n        .header-md {\n            font-size: 24px;\n        }\n\n        .header-sm {\n            padding: 5px 0;\n            font-size: 18px;\n            line-height: 1.3;\n        }\n\n        .content-padding {\n            padding: 20px 0 30px;\n        }\n\n        .mobile-header-padding-right {\n            width: 290px;\n            text-align: right;\n            padding-left: 10px;\n        }\n\n        .mobile-header-padding-left {\n            width: 290px;\n            text-align: left;\n            padding-left: 10px;\n        }\n\n        .free-text {\n            width: 100% !important;\n            padding: 10px 60px 0px;\n        }\n\n        .block-rounded {\n            border-radius: 5px;\n            border: 1px solid #e5e5e5;\n            vertical-align: top;\n        }\n\n        .button {\n            padding: 55px 0 0;\n        }\n\n        .info-block {\n            padding: 0 20px;\n            width: 260px;\n        }\n\n        .mini-block-container {\n            padding: 30px 50px;\n            width: 500px;\n        }\n\n        .mini-block {\n            background-color: #ffffff;\n            width: 498px;\n            border: 1px solid #cccccc;\n            border-radius: 5px;\n            padding: 60px 75px;\n        }\n\n        .block-rounded {\n            width: 260px;\n        }\n\n        .info-img {\n            width: 258px;\n            border-radius: 5px 5px 0 0;\n        }\n\n        .force-width-img {\n            width: 480px;\n            height: 1px !important;\n        }\n\n        .force-width-full {\n            width: 600px;\n            height: 1px !important;\n        }\n\n        .user-img img {\n            width: 82px;\n            border-radius: 5px;\n            border: 1px solid #cccccc;\n        }\n\n        .user-img {\n            width: 92px;\n            text-align: left;\n 